import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const Meteopage = () => {
  const [meteo, setMeteo] = useState();

  const { lat } = useParams();
  console.log(lat);

  const { lon } = useParams();
  console.log(lon);

  const fetchMeteo = () => {
    fetch(
      `https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lon}&appid=91ea4b5d39619a30402a0b31b64e1a45`
    )
      .then((response) => {
        if (response.ok) {
          console.log("fetch conclusa");
          return response.json();
        } else {
          throw new Error("Errore nella richiesta delle prenotazioni al server");
        }
      })
      .then((meteo) => {
        setMeteo(meteo);
      })
      .catch((err) => {
        console.log(err);
      });
    // .finally(() => setIsLoading(false));
  };

  useEffect(() => {
    fetchMeteo();
  });

  return <>{meteo && <h1>{meteo.name}</h1>}</>;
};
export default Meteopage;
