import { useEffect, useState } from "react";
import { Badge, Card, CardText, CardTitle } from "react-bootstrap";
import { useParams } from "react-router-dom";

const Meteopage = () => {
  const [meteo, setMeteo] = useState();

  const { lat } = useParams();

  const { lon } = useParams();

  const fetchMeteo = () => {
    fetch(
      `https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lon}&appid=91ea4b5d39619a30402a0b31b64e1a45`
    )
      .then((response) => {
        if (response.ok) {
          console.log("fetch conclusa");
          return response.json();
        } else {
          throw new Error("Errore nella richiesta delle prenotazioni al server");
        }
      })
      .then((meteo) => {
        setMeteo(meteo);
      })
      .catch((err) => {
        console.log(err);
      });
    // .finally(() => setIsLoading(false));
  };

  useEffect(() => {
    fetchMeteo();
  }, []);

  return (
    meteo && (
      <>
        <Card className="text-center cardMeteo">
          <h1 className="cardMeteoTitle">{meteo.name}</h1>
          <div className="textCardMeteo">
            <Card.Img src={`http://openweathermap.org/img/w/${meteo.weather[0].icon}.png`} />
            <Card.Body>
              <Card.Text className="tempCard">{Math.floor(meteo.main.temp - 273)}°</Card.Text>

              <Badge bg="info">
                <i className="bi bi-thermometer-snow"></i>MIN: {Math.floor(meteo.main.temp_min - 273)}°
              </Badge>
              <Badge bg="warning">
                <i className="bi bi-thermometer-sun"></i>MAX: {Math.floor(meteo.main.temp_max - 273)}°
              </Badge>
            </Card.Body>
          </div>
          <Card.Text>
            <i className="bi bi-thermometer-half"></i>Temperatura percepita: {Math.floor(meteo.main.feels_like - 273)}°
          </Card.Text>
          <Card.Text>
            <i className="bi bi-cloud-haze2"></i>Umidità nell'aria: {meteo.main.humidity}%
          </Card.Text>
          <Card.Text>
            <i className="bi bi-wind"></i>Vento: {meteo.wind.speed} km/h
          </Card.Text>
        </Card>
      </>
    )
  );
};
export default Meteopage;
