import { useEffect, useState } from "react";
import { Badge, Card, CardText, CardTitle } from "react-bootstrap";
import { useParams } from "react-router-dom";

const Meteopage = () => {
  const [meteo, setMeteo] = useState();

  const { lat } = useParams();

  const { lon } = useParams();

  const fetchMeteo = () => {
    fetch(
      `https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lon}&appid=91ea4b5d39619a30402a0b31b64e1a45`
    )
      .then((response) => {
        if (response.ok) {
          console.log("fetch conclusa");
          return response.json();
        } else {
          throw new Error("Errore nella richiesta delle prenotazioni al server");
        }
      })
      .then((meteo) => {
        setMeteo(meteo);
      })
      .catch((err) => {
        console.log(err);
      });
    // .finally(() => setIsLoading(false));
  };

  useEffect(() => {
    fetchMeteo();
  }, []);

  return (
    meteo && (
      <>
        <Card className="text-center">
          <h1 className="cardMeteoTitle">{meteo.name}</h1>
          <div className="textCardMeteo">
            <Card.Img src={`http://openweathermap.org/img/w/${meteo.weather[0].icon}.png`} />
            <Card.Body>
              <Card.Text>{Math.floor(meteo.main.temp - 273)}°</Card.Text>

              <Badge bg="info">MIN: {Math.floor(meteo.main.temp_min - 273)}°</Badge>
              <Badge bg="warning">MAX: {Math.floor(meteo.main.temp_max - 273)}°</Badge>
            </Card.Body>
          </div>
          <Card.Text>{meteo.main.humidity}%</Card.Text>
          <Card.Text>{Math.floor(meteo.main.temp - 273)}°</Card.Text>
          <Card.Text>{Math.floor(meteo.main.temp - 273)}°</Card.Text>
        </Card>
      </>
    )
  );
};
export default Meteopage;
